import React from 'react';
import Button from './Button';

function HeroSection() {
  return (
    <div style={styles.hero}>
      <div style={styles.leftSection}></div>
      <div style={styles.middleSection}>
        <img src="./assets/logo.png" alt="Molly Logo" style={styles.logo} />
        <h1 style={styles.title}>
          Welcome to Molly <span role="img" aria-label="waving hand">ðŸ‘‹</span>
        </h1>
        <p style={styles.description}>
          A simple & Beautiful budgeting app and named it after my dog to help you control your spendings. The only budgeting app you will ever need.
        </p>
        <Button primary>Create an Account</Button>
        <Button>Log In</Button>
      </div>
      <div style={styles.rightSection}></div>
    </div>
  );
}

const styles = {
  hero: {
    display: 'flex',
    height: 'calc(100vh - 60px)', // Adjust based on your navbar height
  },
  leftSection: {
    flex: 1,
  },
  middleSection: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  },
  rightSection: {
    flex: 1,
  },
  logo: {
    width: '100px', // Adjust as needed
    marginBottom: '1rem',
  },
  title: {
    fontSize: '2rem',
    marginBottom: '1rem',
  },
  description: {
    marginBottom: '2rem',
  },
};

export default HeroSection;
